import React, {useState} from 'react';

export function Ip(props) {
    const _userData = JSON.parse(localStorage.getItem("userDetails"));
   const [ip_address, addIp] = useState(["data", "sads"]);
   const [inputBox, showInputBox] = useState(true);
   console.log(inputBox);
    return(
        <div className="container">
            <div className="row">
                <AddIP data={ip_address} action={showInputBox} />
                <showInputBox data={inputBox} />
            </div>
        </div>
    );
}

function AddIP(props) {
    return (
        <div>
            {
                props.data.map((e,i) => {
                    return(
                        <div className="card card-signin my-5">
                            <div className="card-body">
                                <div className="row flex-display">
                                    <div className="col-md-8 form-label-group">
                                        <input type="email" id="i-email" className="form-control" placeholder="Email address" disabled  />
                                        <label htmlFor="i-email" >{e}</label>
                                    </div>
                                    <div className="col-md-1">
                                        <button className="btn btn-lg btn-primary " type="submit">-</button>
                                    </div>
                                    <div className="col-md-1"></div>
                                    {
                                        (function(){
                                            if((i+1)===props.data.length){
                                                return(
                                                    <div className="col-md-1">
                                                        <button className="btn btn-lg btn-primary " type="submit" onClick = {()=>props.action(true)} >+</button>
                                                    </div>
                                                )
                                            }
                                        })()
                                    }
                                </div>
                            </div>
                        </div>
                    )
                })
            }
        </div>
    )
}

function ShowInputBoxDom() {
    return(
        <div className="card card-signin my-5">
          <div className="card-body">
              <div className="form-label-group">
                <input type="email" id="i-email" className="form-control" placeholder="Email address" required />
                <label htmlFor="i-email" >Email address</label>
              </div>
          </div>
        </div>
    )
}

export default Ip;
